// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";

contract ChainlinkOracle is ChainlinkClient, AccessControl {
    string internal _jobId;
    address internal _operatorAddr;
    address internal _linkTokenAddress;
    uint256 private _oraclePayment;

    // Create a new role identifier for the minter role
    bytes32 public constant ORACLE_ADMIN = keccak256("ORACLE_ADMIN");

    event JobIdUpdated(string indexed newValue, address indexed editedBy);

    event OperatorAddressUpdated(
        address indexed newAddr,
        address indexed editedBy
    );

    event RequestFulfilled(
        bytes32 indexed requestId,
        bytes32 indexed responseData
    );

    constructor(
        string memory jobId_,
        address operatorAddr_,
        address linkTokenAddres_
    ) {
        require(bytes(jobId_).length != 0, "Invalid job id");
        require(operatorAddr_ != address(0x0), "Invalid operator address");
        require(linkTokenAddres_ != address(0x0), "Invalid link token address");

        _jobId = jobId_;
        _operatorAddr = operatorAddr_;
        _linkTokenAddress = linkTokenAddres_;

        setChainlinkToken(_linkTokenAddress);
        _oraclePayment = (1 * LINK_DIVISIBILITY) / 10; // 0.1 * 10**18

        // Grant the minter role to a specified account
        _grantRole(ORACLE_ADMIN, msg.sender);
    }

    function getJobId() external view returns (string memory) {
        return _jobId;
    }

    function setJobId(string memory jobId_) external onlyRole(ORACLE_ADMIN) {
        require(bytes(jobId_).length != 0, "Invalid job id");
        _jobId = jobId_;
        emit JobIdUpdated(jobId_, msg.sender);
    }

    function getOperatorAddress() external view returns (address) {
        return _operatorAddr;
    }

    function setOperatorAddress(
        address operatorAddr_
    ) external onlyRole(ORACLE_ADMIN) {
        require(
            operatorAddr_ != address(0x0),
            "Invalid operator contract address"
        );
        _operatorAddr = operatorAddr_;
        emit OperatorAddressUpdated(operatorAddr_, msg.sender);
    }

    function getChainlinkToken() public view returns (address) {
        return chainlinkTokenAddress();
    }

    /**
     * @notice Initiates an request to the oracle operator contract.
     * The type of the request is HTTP GET
     */
    function requestData()
        public
        onlyRole(ORACLE_ADMIN)
        returns (bytes32 requestId)
    {
        Chainlink.Request memory request = buildChainlinkRequest(
            bytes32(abi.encodePacked(_jobId)),
            address(this),
            this.fulfillRequest.selector
        );

        request = _requestData(request);

        requestId = sendChainlinkRequestTo(
            _operatorAddr,
            request,
            _oraclePayment
        );
    }

    function _requestData(
        Chainlink.Request memory request
    ) internal virtual returns (Chainlink.Request memory) {}

    /**
     * @notice Request fulfillment callback function.
     * This is called by the operator contract
     * @param _requestId The id of the request
     * @param responseData The the response
     */
    function fulfillRequest(
        bytes32 _requestId,
        bytes32 responseData
    ) public recordChainlinkFulfillment(_requestId) {
        _fulfillRequest(responseData);
        emit RequestFulfilled(_requestId, responseData);
    }

    function _fulfillRequest(bytes32 responseData) internal virtual {}

    function withdrawLink() public onlyRole(ORACLE_ADMIN) {
        LinkTokenInterface link = LinkTokenInterface(chainlinkTokenAddress());
        require(
            link.transfer(msg.sender, link.balanceOf(address(this))),
            "Unable to transfer"
        );
    }

    function cancelRequest(
        bytes32 _requestId,
        uint256 _payment,
        bytes4 _callbackFunctionId,
        uint256 _expiration
    ) public onlyRole(ORACLE_ADMIN) {
        cancelChainlinkRequest(
            _requestId,
            _payment,
            _callbackFunctionId,
            _expiration
        );
    }
}

contracts/protocol/oracle/PriceOracle.so.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "./ChainlinkOracle.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import {IPriceOracleGetter} from "@aave/core-v3/contracts/interfaces/IPriceOracleGetter.sol";

contract PriceOracle is ChainlinkOracle {
    using Strings for string;
    using Chainlink for Chainlink.Request;

    uint256 public _currentPrice;

    uint256 private _oraclePayment;
    string _httpUrl;

    event HttpUrlUpdated(string indexed newValue, address indexed editedBy);

    constructor(
        string memory httpUrl_,
        string memory jobId_,
        address operatorAddr_,
        address linkTokenAddres_
    ) ChainlinkOracle(jobId_, operatorAddr_, linkTokenAddres_) {
        require(bytes(httpUrl_).length != 0, "Invalid http url");
        _httpUrl = httpUrl_;
    }

    function getHttpUrl() external view returns (string memory) {
        return _httpUrl;
    }

    function setHttpUrl(
        string memory httpUrl_
    ) external onlyRole(ORACLE_ADMIN) {
        require(bytes(httpUrl_).length != 0, "Invalid http url");
        _httpUrl = httpUrl_;
        emit HttpUrlUpdated(httpUrl_, msg.sender);
    }

    function _requestData(
        Chainlink.Request memory request
    ) internal view override returns (Chainlink.Request memory) {
        request.add("get", _httpUrl);
        // Multiply the result by 1000000000000000000 to remove decimals
        int256 timesAmount = 10 ** 18;
        request.addInt("times", timesAmount);
        return request;
    }

    function _fulfillRequest(bytes32 responseData) internal override {
        _currentPrice = uint256(responseData);
    }
}

contracts/protocol/tokens/AFERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "./FERC20.sol";
import "../libraries/Errors.sol";
import "../../interfaces/IAFERC20.sol";
import "../../interfaces/IPriceOracle.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract AFERC20 is IAFERC20, FERC20, ReentrancyGuard {
    using SafeMath for uint256;

    IPriceOracle _priceOracle;
    IERC20 public immutable _token;

    constructor(
        string memory name_,
        string memory symbol_,
        address priceOracle_,
        address token_
    ) FERC20(name_, symbol_) {
        require(
            priceOracle_ != address(0),
            Errors.ZERO_ORACLE_ADDRESS_NOT_VALID
        );
        require(token_ != address(0), Errors.ZERO_ASSET_ADDRESS_NOT_VALID);
        _token = IERC20(token_);
        _priceOracle = IPriceOracle(priceOracle_);
    }

    // Function to receive Ether. msg.data must be empty
    receive() external payable {
        revert(Errors.OPERATION_NOT_SUPPORTED);
    }

    // Fallback function is called when msg.data is not empty
    fallback() external payable {
        revert(Errors.OPERATION_NOT_SUPPORTED);
    }

    /**
     * @notice Mints a certain amount of the specified asset
     * @param beneficiary_ The addresses of the asset
     * @param amount_ The addresses of the asset
     */
    function mintToken(
        address beneficiary_,
        uint256 amount_
    ) public payable override nonReentrant {
        require(amount_ > 0, Errors.ZERO_ASSET_AMOUNT_NOT_VALID);
        require(
            beneficiary_ != address(0),
            Errors.ZERO_BENEFICIARY_ADDRESS_NOT_VALID
        );
        require(
            _token.balanceOf(msg.sender) >= amount_,
            Errors.NOT_ENOUGH_AVAILABLE_USER_BALANCE
        );
        // Get price of asset
        uint256 price = _priceOracle.getPrice();
        require(price > 0, Errors.ZERO_ASSET_PRICE_NOT_VALID);

        uint256 mintValue = amount_.div(price);
        require(mintValue > 0, Errors.INSUFFICIENT_MINT_FUNDS);

        // Transfer tokens to vault
        _token.transferFrom(msg.sender, address(this), amount_);

        // Mint amount of tokens to beneficiary
        _mint(beneficiary_, mintValue);

        emit Mint(msg.sender, beneficiary_, amount_);
    }

    /**
     * @dev Destroys `amount_` tokens from the caller.
     *
     * See {ERC20-_burn}.
     */
    function burn(uint256 amount_) public override {
        require(amount_ > 0, Errors.ZERO_TOKEN_AMOUNT_NOT_VALID);
        require(
            balanceOf(msg.sender) >= amount_,
            Errors.NOT_ENOUGH_AVAILABLE_USER_BALANCE
        );
        require(
            msg.sender != address(0),
            Errors.ZERO_BENEFICIARY_ADDRESS_NOT_VALID
        );

        uint256 price = _priceOracle.getPrice();
        require(price > 0, Errors.ZERO_ASSET_PRICE_NOT_VALID);
        // Calculate the amount of asset to transfer after burning token
        uint256 underlyingAmount = amount_.mul(price);
        require(underlyingAmount > 0, Errors.INSUFFICIENT_BURN_FUNDS);
        require(
            _token.balanceOf(address(this)) >= underlyingAmount,
            Errors.INSUFFICIENT_FUNDS
        );

        _burn(msg.sender, amount_);

        _token.transfer(msg.sender, underlyingAmount);

        emit Burn(_msgSender(), msg.sender, amount_);
    }
}

